const PaymentCard = (props: Props) => {
    const [choosePaymentMethod, setChoosePaymentMethod] = useState<string>('');
    return (
        <Card
            variant='border'
            rounded='lg'
            noPadding
            body={
                <div className='flex flex-col gap-4 p-4 card-animation'>
                    <Typography htmlTag='h5' variant='subtitle20'>
                        Hình thức thanh toán
                    </Typography>

                    <RadioGroup name='payment-method'>
                        <div className='flex flex-col gap-3'>
                            {paymentMethods.map((paymentMethod) => (
                                <>
                                    <div
                                        onClick={() => {
                                            setChoosePaymentMethod(paymentMethod.id);
                                        }}
                                        className='flex justify-between cursor-pointer'
                                    >
                                        <div className='flex gap-1'>
                                            {paymentMethod.icon}

                                            <Typography htmlTag='span' variant='body16'>
                                                {paymentMethod.name}
                                                <Typography
                                                    htmlTag='span'
                                                    variant='body16'
                                                    customClasses={{ root: '!text-warning pl-1' }}
                                                >
                                                    {paymentMethod.description && `(${paymentMethod.description})`}
                                                </Typography>
                                            </Typography>
                                        </div>
                                        <Radio
                                            // onChange={(e) => {
                                            //     setChoosePaymentMethod(e.target.value);
                                            // }}
                                            checked={choosePaymentMethod === paymentMethod.id}
                                            value={paymentMethod.id}
                                        />
                                    </div>

                                    {choosePaymentMethod === paymentMethod.id && paymentMethod.body}
                                    <Divider />
                                </>
                            ))}
                        </div>
                    </RadioGroup>
                    <div className=''>
                        <Checkbox size='lg' label='Đồng ý với  điều khoản của Flynow' checked />
                    </div>
                </div>
            }
        />
    );
};

export default PaymentCard;
